<template>  <div class="login-container">    <div class="title">{{ musicName }}</div>    <div class="login">      <el-form :model="loginForm" :rules="rules" label-width="22%" hide-required-asterisk>        <el-form-item prop="username" label="用户名">          <el-input v-model="loginForm.username" placeholder="username"></el-input>        </el-form-item>        <el-form-item prop="password" label="密码">          <el-input type="password" placeholder="password" v-model="loginForm.password" @keyup.enter="submitForm"></el-input>        </el-form-item>        <el-button type="primary" @click="submitForm" class="login-btn">登录</el-button>      </el-form>    </div>  </div></template><script lang="ts">import { RouterName } from "@/enums";import {useRouter} from "@/hooks/useRouter";import {HttpManager} from "@/api";import {ComponentInternalInstance, getCurrentInstance} from "vue";export default defineComponent({  name: "Login",  setup() {    const { proxy } = getCurrentInstance() as ComponentInternalInstance;    const { routerManager } = useRouter();    const musicName = ref("麻薯音乐管理系统")    const loginForm = reactive({      username: "",      password: "",    });    const rules = reactive({      username: [{ required: true, message: "请输入用户名", trigger: "blur" }],      password: [{ required: true, message: "请输入密码", trigger: "blur" }],    });    async function submitForm() {        let username = loginForm.username;        let password = loginForm.password;        const result = (await HttpManager.getLoginStatus({username,password})) as ResponseBody;        ElMessage({            message: result.message,            type: result.type as "success" | "warning" | "error" | "info"        });        if (result.success) routerManager(RouterName.Info, { path: RouterName.Info });    }    return {      musicName,      loginForm,      rules,      submitForm    }  },});</script><style lang="scss" scoped>@import "@/assets/css/login.scss";</style>